import preferences from "preferences";
import {current_language} from "translate";

let warned = false;
let profanity_regex: any = {};
let filters: any = {};

preferences.watch("profanity-filter", (_filters) => {
    filters = {};
    for (let k in _filters) {
        if (_filters[k]) {
            if (k === "locale") {
                filters[current_language] = true;
            } else {
                filters[k] = true;
            }
        }
    }
});

export function profanity_filter(str) {
    for (let k in filters) {
        if (k in profanity_regex) {
            str = str.replace(profanity_regex[k], "$*!%", str);
        }
    }
    return str;
}
